package com.orrz.shopping_cart.Cart.model;import com.orrz.shopping_cart.Product.model.Product;import com.orrz.shopping_cart.User.view.UserView;/** * @Author: Lin_Ya * @Date: 2019-01-31-16:09 * @Version: 1.0 * @Description: 购物车类，用于表示 一条购物车记录 */public class CartItem {    private long id;    private UserView user;    private Product product;    private long quantity;    public CartItem(UserView user, Product product, long quantity) {        this.user = user;        this.product = product;        this.quantity = quantity;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public UserView getUser() {        return user;    }    public void setUser(UserView user) {        this.user = user;    }    public Product getProduct() {        return product;    }    public void setProduct(Product product) {        this.product = product;    }    public long getQuantity() {        return quantity;    }    public void setQuantity(long quantity) {        this.quantity = quantity;    }}