package com.orrz.shopping_cart.Order.model;import com.orrz.shopping_cart.Product.model.Product;import com.orrz.shopping_cart.User.view.UserView;/** * @Author: Lin_Ya * @Date: 2019-02-01-16:15 * @Version: 1.0 * @Description: 订单类 */public class Order {    private long id;    private UserView user;    private long quantity;    private Product product;    private String status;    private String address;    public Order() {    }    public Order(UserView user, long quantity, Product product, String status, String address) {        this.user = user;        this.quantity = quantity;        this.product = product;        this.status = status;        this.address = address;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public UserView getUser() {        return user;    }    public void setUser(UserView user) {        this.user = user;    }    public long getQuantity() {        return quantity;    }    public void setQuantity(long quantity) {        this.quantity = quantity;    }    public Product getProduct() {        return product;    }    public void setProduct(Product product) {        this.product = product;    }    public String getStatus() {        return status;    }    public void setStatus(String status) {        this.status = status;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }}