package com.orrz.shopping_cart.Order.model;import org.springframework.stereotype.Component;import java.util.ArrayList;import java.util.HashMap;import java.util.List;/** * @Author: Lin_Ya * @Date: 2019-02-01-16:20 * @Version: 1.0 * @Description: OrderDao 模拟类 */@Component("OrderDao")public class OrderMockDao implements OrderDao {    private HashMap<Long, Order> orders = new HashMap<>();    @Override    public Order getById(long id) {        if (orders.containsKey(id)) {            return orders.get(id);        } else {            return null;        }    }    @Override    public Order save(Order order) {        if (order.getId() > 0) {            return orders.put(order.getId(), order);        } else {            long id = (long) (orders.size() + 1);            order.setId(id);            orders.put(id, order);            return orders.get(id);        }    }    @Override    public List<Order> findAll() {        return new ArrayList<>(orders.values());    }    @Override    public Boolean delete(long id) {        if (orders.containsKey(id)) {            orders.remove(id);            return true;        } else {            return false;        }    }}