package com.orrz.shopping_cart.Order.view.create;/** * @Author: Lin_Ya * @Date: 2019-02-01-16:32 * @Version: 1.0 * @Description: 创建订单的请求类 */public class CreateOrderRequest {    private Long userId;    private Long productId;    private Long quantity;    private String status;    private String address;    public CreateOrderRequest() {    }    public CreateOrderRequest(Long userId, Long productId, Long quantity, String status, String address) {        this.userId = userId;        this.productId = productId;        this.quantity = quantity;        this.status = status;        this.address = address;    }    public Long getUserId() {        return userId;    }    public void setUserId(Long userId) {        this.userId = userId;    }    public Long getProductId() {        return productId;    }    public void setProductId(Long productId) {        this.productId = productId;    }    public Long getQuantity() {        return quantity;    }    public void setQuantity(Long quantity) {        this.quantity = quantity;    }    public String getStatus() {        return status;    }    public void setStatus(String status) {        this.status = status;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }}