package com.orrz.shopping_cart.Order.view.create;import org.springframework.stereotype.Component;/** * @Author: Lin_Ya * @Date: 2019-02-01-16:31 * @Version: 1.0 * @Description: 用于判断能否创建订单 */@Componentpublic class CreateOrderValidator {    public CreateOrderValidator() {    }    public boolean validate(CreateOrderRequest createOrderRequest) {        long userId = createOrderRequest.getUserId();        long productId = createOrderRequest.getProductId();        long quantity = createOrderRequest.getProductId();        String address = createOrderRequest.getAddress();        if (userId < 1 || productId < 1 || quantity < 1 || address.length() < 1) {            return false;        }        /**         * 中间还应该添加一部分逻辑，例如：         * 判断用户是否有效         * 判断商品是否有效         * 判断库存情况         * 判断送货地址是否在配送范围         */        return true;    }}