package com.orrz.shopping_cart.Product.model;/** * @Author: Lin_Ya * @Date: 2019-01-31-09:05 * @Version: 1.0 * @Description: 产品类 */public class Product {    private long id;    private String name;    private String description;    private double price;    public Product(String name, String description, double price) {        this.name = name;        this.description = description;        this.price = price;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public double getPrice() {        return price;    }    public void setPrice(double price) {        this.price = price;    }}