package com.orrz.shopping_cart.Product.view;import com.orrz.shopping_cart.Product.model.Product;import java.util.List;/** * @Author: Lin_Ya * @Date: 2019-01-31-09:43 * @Version: 1.0 * @Description: 响应类 返回全部商品 */public class ListProductsResponse {    List<Product> products;    public ListProductsResponse() {    }    public ListProductsResponse(List<Product> products) {        this.products = products;    }    public List<Product> getProducts() {        return products;    }    public void setProducts(List<Product> products) {        this.products = products;    }}