package com.orrz.shopping_cart.Product.view.create;/** * @Author: Lin_Ya * @Date: 2019-01-31-09:51 * @Version: 1.0 * @Description: 创建商品的请求类 */public class CreateProductRequest {    private String name;    private String description;    private double price;    public CreateProductRequest() {    }    public CreateProductRequest(String name, String description, double price) {        this.name = name;        this.description = description;        this.price = price;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public double getPrice() {        return price;    }    public void setPrice(double price) {        this.price = price;    }}