package com.orrz.shopping_cart.User.model;/** * @Author: Lin_Ya * @Date: 2019-01-29-11:48 * @Version: 1.0 * @Description: User 类，定义了 用户所具有的属性和行为 */public class User {    private long id;    private String name;    private String password;    private String phoneNumber;    public User() {    }    public User(String name, String password, String phoneNumber) {        this.name = name;        this.password = password;        this.phoneNumber = phoneNumber;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getPhoneNumber() {        return phoneNumber;    }    public void setPhoneNumber(String phoneNumber) {        this.phoneNumber = phoneNumber;    }}