package com.orrz.shopping_cart.User.view.create;/** * @Author: Lin_Ya * @Date: 2019-01-29-15:56 * @Version: 1.0 * @Description: 创建用户请求类 */public class CreateUserRequest {    private String name;    private String password;    private String phoneNumber;    public CreateUserRequest() {    }    public CreateUserRequest(String name, String password, String phoneNumber) {        this.name = name;        this.password = password;        this.phoneNumber = phoneNumber;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getPhoneNumber() {        return phoneNumber;    }    public void setPhoneNumber(String phoneNumber) {        this.phoneNumber = phoneNumber;    }}